import React, { useEffect, useState, forwardRef } from 'react';
import './Hero.css';
import heroImage from '../images/hero0.png';
import gitlogo from '../images/github.png';
import instalogo from '../images/insta_logo.png';

const Hero = forwardRef((scrollToSection, refs) => {
  const [scrollY, setScrollY] = useState(0);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('scroll', handleScroll);
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const scaleValue = Math.max(isSmallScreen ? 1 - scrollY / 300 : 1 - scrollY / 200, 0);

  useEffect(() => {
    const navbarName = document.querySelector('.navbar-name');
    if (scrollY > 150) {
      navbarName.classList.add('visible');
    } else {
      navbarName.classList.remove('visible');
    }
  }, [scrollY]);

  const handleAnchorClick = (event, ref) => {
    event.preventDefault();
    scrollToSection(ref);
  };

  return (
    <div className="HeroSection" ref={ref}>
      {/* Social Media Icons (Left) */}
      <div className="social-media-other">
        <div className="line"></div>
        <a href="https://github.com" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={gitlogo} alt="Github logo" />
        </a>
        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={instalogo} alt="Instagram logo" />
        </a>
      </div>

      {/* Hero Text Section */}
      <div className="hero-text">
        <p>Hello WorldðŸ‘‹,</p>
        <div className="hero-name">
          <p>I'm </p>&nbsp;&nbsp;
          <h1
            style={{
              transform: `scale(${scaleValue})`,
              transition: 'transform 0.2s ease-in-out',
            }}
          >
            Pavan Sundar
          </h1>
        </div>
        <p className="role-description">
          Web Developer & <br />
          Full-Stack Enthusiast
        </p>
        <button className="cta" onSubmit={(e) => handleAnchorClick(e, refs.contactMeRef)}>Contact Me</button>
      </div>

      {/* Hero Image Section */}
      <div className="hero-img">
        <img src={heroImage} alt="Hero section background" />
      </div>

      {/* Social Media Icons (Right) */}
      <div className="social-media">
        <div className="line"></div>
        <a href="https://github.com/pavansundar9/" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={gitlogo} alt="Github logo" />
        </a>
        <a href="https://www.linkedin.com/in/pavan-sundar/" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={instalogo} alt="LinkedIn logo" />
        </a>
      </div>
    </div>
  );
});

export default Hero;
