import './AboutMe.css';
import aboutMe from '../images/about_me_0.png';
const AboutMe = React.forwardRef((props, ref) => {
    return (
        <div className="aboutme" ref={ref}>
            <h1>About Me</h1>
            <div className='about-info'>
                <div className='about-image'>
                    <img src={aboutMe} alt='About me' />
                </div>
                <div className='about-text'>
                    <p>
                        Hi there! I'm <p className='name'>Pavan Sundar</p>, a passionate Web Developer and Full Stack Enthusist dedicated to transforming ideas into impactful digital experiences. I recently took my graduation from Vellore Institute of Technology(Sptember 2024), I specialize in creating user-centric applications that merge functionality with aesthetic appeal.
                        <br /><br />
                        From developing sleek and responsive interfaces using HTML, CSS, and JavaScript to crafting robust back-end solutions with React, PHP, and MySQL, I thrive on building applications that solve real-world problems.
                        <br /><br />
                        Beyond coding, I'm constantly exploring emerging tech trends, taking on exciting programming challenges, and envisioning innovative solutions for tomorrow.
                        <br /><br />
                        Let's collaborate to turn great ideas into reality! ðŸŒŸ
                    </p>
                </div>
            </div>
        </div>
    );
});

export default AboutMe;
