import React, { useEffect, useState, forwardRef } from 'react';
import './Hero.css';
import heroImage from '../images/hero0.png';
import gitlogo from '../images/github.png';
// import instalogo from '../images/insta_logo.png';
import linkedinlogo from '../images/linkedin_logo.png';

const Hero = forwardRef(({ scrollToContact, contactMeRef }, ref) => {
  const [scrollY, setScrollY] = useState(0);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('scroll', handleScroll);
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Calculate transition progress (0 to 1) based on scroll position
  const transitionProgress = Math.min(scrollY / 150, 1);

  // Initial and final positions and scales for the animation
  const initialX = 0;
  const initialY = 0;
  const initialScale = isSmallScreen ? 1 : 1;

  // Target positions (navbar position)
  const targetX = isSmallScreen ? -28 : -200;
  const targetY = isSmallScreen ? -190 : -220;
  const targetScale = isSmallScreen ? 0.6 : 0.65;

  // Calculate current position and scale based on scroll progress
  const currentX = initialX + (targetX - initialX) * transitionProgress;
  const currentY = initialY + (targetY - initialY) * transitionProgress;
  const currentScale = initialScale + (targetScale - initialScale) * transitionProgress;

  // Calculate opacity for the rest of the hero content
  const heroContentOpacity = Math.max(1 - transitionProgress * 1.5, 0);

  useEffect(() => {
    const navbarName = document.querySelector('.navbar-name');
    if (scrollY > 150) {
      navbarName.classList.add('visible');
    } else {
      navbarName.classList.remove('visible');
    }
  }, [scrollY]);

  return (
    <div className="HeroSection" ref={ref}>
      {/* Social Media Icons (Left) */}
      <div className="social-media-other">
        <div className="line"></div>
        <a href="https://github.com/pavansundar9/" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={gitlogo} alt="Github logo" />
        </a>
        <a href="https://www.linkedin.com/in/pavan-sundar/" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={linkedinlogo} alt="LinkedIn logo" />
        </a>
      </div>

      {/* Hero Text Section */}
      <div className="hero-text">
        <p style={{ opacity: heroContentOpacity }}>Hello World <span className='wave'>ðŸ‘‹</span> ,</p>
        <div className="hero-name">
          <p style={{ opacity: heroContentOpacity }}>I'm </p>
          <h1
            className="animated-name"
            style={{
              transform: `translate(${currentX}px, ${currentY}px) scale(${currentScale})`,
              transition: 'transform 0.3s ease-out',
              position: 'relative',
              zIndex: 10,
            }}
          >
            Pavan Sundar
          </h1>
        </div>
        <p className="role-description" style={{ opacity: heroContentOpacity }}>
          Web Developer & <br />
          Full-Stack Enthusiast
        </p>
        <div className='buttons-1' style={{ opacity: heroContentOpacity }}>
          <button className="cta" onClick={() => scrollToContact(contactMeRef)}>
            Contact Me
          </button>
          <a href='https://drive.google.com/file/d/1dKxtyKkUKYHKiH5mVqHCrR2VTEA0aqQp/view' target='_blank' className='resume' rel="noreferrer">
            Resume <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-external-link"><path d="M15 3h6v6"/><path d="M10 14 21 3"/><path d="M18 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h6"/></svg>
          </a>
        </div>
      </div>

      {/* Hero Image Section */}
      <div className="hero-img" style={{ opacity: heroContentOpacity }}>
        <img src={heroImage} alt="Hero section background" />
      </div>

      {/* Social Media Icons (Right) */}
      <div className="social-media">
        <div className="line"></div>
        <a href="https://github.com/pavansundar9/" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={gitlogo} alt="Github logo" />
        </a>
        <a href="https://www.linkedin.com/in/pavan-sundar/" target="_blank" rel="noopener noreferrer">
          <img className="social-icon" src={linkedinlogo} alt="LinkedIn logo" />
        </a>
      </div>
    </div>
  );
});

export default Hero;
